export const fastestTime = (hikers: any, bridges: any) => {
    // Sort hikers by speed
    hikers.sort((a: any, b: any) => a.speed - b.speed);

    let totalTime = 0;

    for (let i = 0; i < bridges.length; i++) {
      const bridge = bridges[i];
      // Check if there are additional hikers on this bridge
      let hikersOnBridge = hikers.filter((hiker: any) => bridge.hikers.includes(hiker.id));
      if (bridge.additional_hikers) {
        hikersOnBridge = hikersOnBridge.concat(hikers.filter((hiker: any) => bridge.additional_hikers.includes(hiker.id)));
      }

      // Cross the bridge with two slowest hikers at a time
      while (hikersOnBridge.length > 0) {
        if (hikersOnBridge.length === 1) {
          // Last hiker crosses alone
          totalTime += bridge.length / hikersOnBridge[0].speed;
          hikersOnBridge = [];
        } else {
          // Two slowest hikers cross together
          totalTime += Math.max(bridge.length / hikersOnBridge[0].speed, bridge.length / hikersOnBridge[1].speed);
          hikersOnBridge = hikersOnBridge.slice(2);
        }
      }
    }

    return totalTime;
  }
