import { useEffect, useState } from 'react';
import './App.css';
import Providers from './components/provider/providers';

function App() {
  const [hikers, setHikers]: any = useState(null);
  const [bridges, setBridges]: any = useState(null);

  useEffect(() => {
    // Get hikers
    fetch('/api/hikers')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(hikers => {
        if(hikers) setHikers(hikers);
      })
      .catch(error => {
        console.error('There was a problem with the fetch operation:', error);
      });

    // Get bridges
    fetch('/api/bridges')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(bridges => {
        if(bridges) setBridges(bridges);
      })
      .catch(error => {
        console.error('There was a problem with the fetch operation:', error);
      });
  }, []);

  console.log(hikers);
  console.log(bridges);

  return (
    <div className="App">
      <div style={{
        display: 'flex',
        flexDirection: 'column',
        width: 800,
        border: '1px solid #E6E6E6',
        backgroundColor: 'white',
        alignItems: 'flex-start',
        padding: 20
      }}>
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center'}}>
          { bridges && bridges.bridgeData && bridges.bridgeData.map((bridge: any) => {
                return <Bridge bridge={bridge} hikers={hikers}/>
            })
          }
        </div>
      </div>
    </div>
  );
}

function Bridge(props: any) {
  if(!props?.bridge){
    return null;
  }

  const hikers = props.bridge?.hikers;
  return (
    <div style={{marginBottom: 50}}>
      <>
        <img src={require("./assets/bridge.png")} alt="provider" width={80} height={80} style={{borderRadius: 40}} />

        <div style={{textAlign: 'center'}}>
          <p style={{ fontWeight: 'bold', color: 'black', margin: '0 0'}}>{props.bridge.id}</p>
          <p style={{ color: 'black', marginTop: 10, marginBottom: 10, fontSize: 14}}>Length: {props.bridge.length}m</p>
        </div>
      </>

      <div style={{display: 'flex', flexDirection: 'row', alignItems: 'center'}}>
        {hikers && hikers.length && hikers.map((hiker: any) => {
          if(!hiker || !props.hikers) return null;

          const bridgeHiker = props.hikers.hikerData?.find((h1: any) => h1.id === hiker);
          return <Hiker hiker={bridgeHiker}/>
        })}
      </div>
    </div>
  );
}

function Hiker(props: any) {
  if(!props?.hiker){
    return null;
  }

  return (
    <div style={{ display: 'flex', flexDirection: 'row', alignItems: 'center'}}>
      <div style={{ marginRight: '10px' }}>
        <img src={require("./assets/hiker.png")} alt="provider" width={80} height={80} style={{borderRadius: 40}} />
      </div>
      <div style={{marginLeft: 10, marginRight: 10}}>
        <div style={{textAlign: 'left'}}>
          <p style={{ fontWeight: 'bold', color: '#35E7F6', margin: '0 0'}}>{"Hello"}</p>
          <p style={{ color: 'black', marginTop: 10, marginBottom: 10, fontSize: 14}}>{"Hello"}</p>
          <button style={{ backgroundColor: '#395D75', color: 'white', width: 80, height: 30, fontWeight: 'bold' }}>{"Hello"}</button>
        </div>
      </div>
    </div>
  );
}

export default App;
