import { createStore } from "redux";

interface AppState {
  hikers: any[];
  bridges: any[];
}

const initialState: AppState = {
  hikers: [],
  bridges: [],
};

function rootReducer(state: AppState = initialState, action: any) {
  switch (action.type) {

    case "ADD_HIKERS":
      console.log("Adding hikers:", action.payload);
      return {
        ...state,
        hikers: action.payload,
      };

    case "ADD_BRIDGES":
      return {
        ...state,
        bridges: action.payload,
      };

    case "UPDATE_BRIDGE":
      const currentBridges = state.bridges;
      console.log("Current bridges:", currentBridges);
      console.log("Updated bridge:", action.payload);
      return {
        ...state,
        bridges: action.payload,
      };

    default:
      return state;
  }
}

export const store = createStore(rootReducer);
