import { useEffect, useState } from 'react';
import './App.css';
import Providers from './components/provider/providers';

function App() {
  const [hikers, setHikers]: any = useState(null);
  const [bridges, setBridges]: any = useState(null);

  useEffect(() => {
    // Get hikers
    fetch('/api/hikers')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(hikers => {
        if(hikers) setHikers(hikers);
      })
      .catch(error => {
        console.error('There was a problem with the fetch operation:', error);
      });

    // Get bridges
    fetch('/api/bridges')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(bridges => {
        if(bridges) setBridges(bridges);
      })
      .catch(error => {
        console.error('There was a problem with the fetch operation:', error);
      });
  }, []);

  console.log(hikers);
  console.log(bridges);

  return (
    <div className="App" style={{display: 'flex', alignItems: 'center', justifyContent: 'center', margin: 20}}>
      <div style={{
        display: 'flex',
        flexDirection: 'column',
        width: 800,
        border: '1px solid #E6E6E6',
        backgroundColor: 'white',
        padding: 20
      }}>

        <p>Scroll horizontally to see hikers</p>
        <div style={{ display: 'flex', flexDirection: 'column' }}>
          { bridges && bridges.bridgeData && bridges.bridgeData.map((bridge: any) => {
                return (
                  <Bridge bridge={bridge} hikers={hikers}/>
                )
            })
          }

          <button style={{ backgroundColor: '#395D75', color: 'white', width: 120, height: 50, fontWeight: 'bold', borderRadius: 10 }}>Add Bridge</button>
        </div>
      </div>
    </div>
  );
}

function Bridge(props: any) {
  const [speed, setSpeed] = useState('');

  if(!props?.bridge){
    return null;
  }

  const handleInputChange = (event: any) => {
    setSpeed(event.target.value);
  }

  const hikerSpeed = () => {
    console.log(`The hiker's speed is ${speed} km/h.`);
  }

  const hikers = props.bridge?.hikers;
  return (
    <div style={{display: 'flex', flexDirection: 'row', marginBottom: 50, alignItems: 'center', justifyContent: 'flex-start', border: '2px #E7E7E7', borderRadius: 10, padding: 10}}>
      <div>
        <img src={require("./assets/bridge.png")} alt="provider" width={80} height={80} style={{borderRadius: 40}} />

        <div style={{textAlign: 'center'}}>
          <p style={{ fontWeight: 'bold', color: 'black', margin: '0 0'}}>{props.bridge.id}</p>
          <p style={{ color: 'black', marginTop: 10, marginBottom: 10, fontSize: 14}}>Length: {props.bridge.length}m</p>
        </div>
      </div>

      <div style={{display: 'flex', flexDirection: 'row', overflowX: 'scroll', whiteSpace: 'nowrap'}}>
        {hikers && hikers.length && hikers.map((hiker: any) => {
          if(!hiker || !props.hikers) return null;

          const bridgeHiker = props.hikers.hikerData?.find((h1: any) => h1.id === hiker);
          return <Hiker hiker={bridgeHiker}/>
        })}
      </div>


        <div style={{display: 'flex', flexDirection: 'column', justifyContent: 'space-evenly', alignItems: 'center'}}>
          <label style={{marginBottom: 5}}>
            Enter hiker speed:
          </label>
          <input type="number" value={speed} onChange={handleInputChange} />
          <button style={{ backgroundColor: '#395D75', color: 'white', width: 100, height: 30, fontWeight: 'bold', borderRadius: 10, marginTop: 10}}>Add Hiker</button>
        </div>
    </div>
  );
}

function Hiker(props: any) {
  if(!props?.hiker){
    return null;
  }

  return (
    <div style={{ display: 'flex', flexDirection: 'row', alignItems: 'center', backgroundColor: "#F1F1F1", margin: 10, borderRadius: 10, paddingTop: 20, paddingBottom: 20}}>
      <div style={{ marginRight: '10px' }}>
        <img src={require("./assets/hiker.png")} alt="provider" width={50} height={50} style={{borderRadius: 40}} />
      </div>
      <div style={{marginLeft: 10, marginRight: 10}}>
        <div style={{textAlign: 'left'}}>
          <p style={{ fontWeight: 'bold', color: '#35E7F6', margin: '0 0'}}>{props.hiker.id}</p>
          <p style={{ color: 'black', marginTop: 10, marginBottom: 10, fontSize: 14}}>Speed: {props.hiker.speed}m/s</p>
        </div>
      </div>
    </div>
  );
}

export default App;
